// src/components/CustomerDetails.jsx
import React, { useEffect, useState } from "react";
import api from "../api/api";

export default function CustomerDetails({ id }) {
  const [customer, setCustomer] = useState(null);

  useEffect(() => {
    const fetchCustomer = async () => {
      try {
        const res = await api.get(`/customers/${id}`);
        setCustomer(res.data);
      } catch (err) {
        console.error("Error fetching customer details:", err);
      }
    };

    if (id) {
      fetchCustomer();
    }
  }, [id]);

  if (!customer) return <p>Loading customer details...</p>;

  return (
    <div className="p-4 border rounded mt-4 bg-gray-50">
      <h3 className="text-lg font-bold mb-2">Customer Details</h3>
      <p><strong>ID:</strong> {customer.id}</p>
      <p><strong>Name:</strong> {customer.firstName} {customer.lastName}</p>
      <p><strong>Email:</strong> {customer.email}</p>
      <p><strong>Age:</strong> {customer.age}</p>
      <p><strong>Gender:</strong> {customer.gender}</p>
      <p><strong>City:</strong> {customer.city}</p>
      <p><strong>Country:</strong> {customer.country}</p>
      <p><strong>Created At:</strong> {new Date(customer.createdAt).toLocaleString()}</p>
      <p><strong>Total Orders:</strong> {customer.orderCount}</p>
    </div>
  );
}
